import { ADD_PROFESSIONAL_DATA } from '../actions';

const INITIAL_STATE = {
  curriculum: '',
  job: '',
  description: '',
};

const professionalData = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case ADD_PROFESSIONAL_DATA:
    return {
      ...state,
      ...action.value,
    };
  default:
    return {
      ...state,
    };
  }
};

export default professionalData;
