import React, { useState } from 'react';

// 游냗 receba `animal` e `onAnimalChange` como  props nesse componente
function FavoriteAnimal() {
  // 游눢 apague essa parte, j치 que agora ela ser치 gerenciada no App.js
  const [animal, setAnimal] = useState('');
  return (
    <div>
      <label htmlFor="animal">
        Favorite Animal:
        <input
          id="animal"
          value={ animal }
          // 游냗 Passe a utilizar a fun칞칚o onAnimalChange
          onChange={ (event) => setAnimal(event.target.value) }
        />
      </label>
    </div>
  );
}

export default FavoriteAnimal;
