import { useEffect, useState } from 'react';

function useHookName(defaultValue) {
  const [name, setName] = useState(defaultValue);

  useEffect(() => {
    localStorage.setItem(defaultValue);
  }, []);

  return [name, setName];
}

export default useHookName;
